<?php	/* *	The template for displaying events calendar on "/events" url slug * *	Override this tempalte by coping it to yourtheme/eventon/archive-ajde_events.php * *	@Author: AJDE *	@EventON *	@version: 0.1 */	//grab the end time from evcal_erow//compare it to the current time//only return results evcal_erow > current time$currenttime = time() - 5 * 3600;$evOpt = evo_get_options('1');$archive_page_id = evo_get_event_page_id($evOpt);$args = array(	'post_type' => 'ajde_events',	'meta_query' => array(		array(			'key' => 'evcal_erow',			'value' => $currenttime,			'compare' => '>=',			'type' => 'numeric'				))	);$the_query = new WP_Query($args);	if($the_query->have_posts()) { ?>	<div class="col-md-6 events-box">	<div class="home-event-calendar">		<h2>Upcoming Events</h2><?php		// check whether archive post id passed	if($archive_page_id){		$archive_page  = get_page($archive_page_id);			echo "<div class='wrapper'>";		echo apply_filters('the_content', $archive_page->post_content);		echo "</div>";?>	</div>	</div><div class="col-md-6 contact-box">	<?php get_template_part('templates/home-contact'); ?></div><?php	} else {		echo "<p>ERROR: Please select a event archieve page in eventON Settings</p>";	}} else {	echo "";?><div class="col-md-12 contact-box">	<?php get_template_part('templates/home-contact'); ?></div><?php } ?>